var e=class{constructor(){this.DOM={container:document.getElementById("video-container"),video:document.getElementById("promo-video"),btn:document.getElementById("video-play-button")},this.init()}init(){!this.DOM.container||!this.DOM.video||!this.DOM.btn||this.bindEvents()}bindEvents(){this.DOM.container.addEventListener("click",()=>this.togglePlay()),this.DOM.video.addEventListener("play",()=>this.updateUI(!0)),this.DOM.video.addEventListener("pause",()=>this.updateUI(!1)),this.DOM.video.addEventListener("ended",()=>this.updateUI(!1))}togglePlay(){this.DOM.video.paused?this.DOM.video.play():this.DOM.video.pause()}updateUI(t){t?this.DOM.btn.classList.add("hidden"):this.DOM.btn.classList.remove("hidden")}};export{e as VideoPlayerController};
